contract MinToken{
		//name
    string public name = "Min Token";
    string public symbol = "MIN";
    string public standard = "Min Token v1.0";
    uint256 public totalSupply;
    // address _link = 0x20fE562d797A42Dcb3399062AE9546cd06f63280;
    // address _oracle = 0xC58b672f829B0f7b64339685Afb3522E7f978A00;
    // bytes32 constant SPEC_ID = bytes32("9a0e26db88ec422fb578b3a4fe54a349");
    // uint256 public currentPrice;

    event RequestEthereumPriceFulfilled(
        bytes32 indexed requestId,
        uint256 indexed price
    );

    event Transfer(
        address indexed _from,
        address indexed _to,
        uint256 _value
    );

    event Approval(
        address indexed _owner,
        address indexed _spender,
        uint256 _value
    );

    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;


    constructor (uint256 _initialSupply) public {
        // setOracle(_oracle);
        // setLinkToken(_link);
        balanceOf[msg.sender] = _initialSupply;
        totalSupply = _initialSupply;
        //allocate the _initialSupply
    }

    //transfer
    function transfer (address _to, uint256 _value) public returns (bool success) {
        //exception if account doesnt have enough
        require(balanceOf[msg.sender] >= _value, "user not authorized");
        //transfer the balance
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
        //transfer event
        emit Transfer(msg.sender, _to,_value);
        //return a boolean of success
        return true;
    }

    //delegated transfer
    //appprove
    function approve (address _spender, uint256 _value) public returns(bool success) {
        //allowance
        allowance[msg.sender][_spender] = _value;
        //approve event
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    function transferFrom (address _from, address _to, uint256 _value) public returns(bool success) {
        //require _from has enough tokens
        require (balanceOf[_from] >= _value, "User not authorized");
        //require allowance is big enough
        require (allowance[_from][msg.sender] >= _value, "User not authorized");
        //change the balance
        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        //update de allowance
        allowance[_from][msg.sender] -= _value;
        //Transfer event
        emit Transfer(_from, _to, _value);
        return true;
    }

    function getBalance() public view returns(uint256 balance) {
        return balanceOf[msg.sender];
    }

